CREATE TABLE `products` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `name` varchar(150) NOT NULL,
  `price` varchar(30) NOT NULL,
  `price_old` varchar(30) DEFAULT NULL,
  `code` varchar(10) NOT NULL,
  `cat_id` varchar(10) DEFAULT NULL,
  `desc` text DEFAULT NULL,
  `content` text DEFAULT NULL,
  `cpu` varchar(150) NOT NULL,
  `ram` varchar(150) NOT NULL,
  `rom` varchar(150) NOT NULL,
  `weight` varchar(150) NOT NULL,
  `display` varchar(150) NOT NULL,
  `slug` varchar(150) NOT NULL,
  `opera` varchar(50) NOT NULL,
  `trandmake` varchar(200) NOT NULL,
  `thumb_1` varchar(200) DEFAULT NULL,
  `thumb_2` varchar(200) DEFAULT NULL,
  `thumb_3` varchar(200) DEFAULT NULL,
  `thumb_4` varchar(200) DEFAULT NULL,
  `avatar` varchar(200) DEFAULT NULL,
  `cat_product` enum('laptop','mobile','smartwatch') NOT NULL,
  `state` enum('state1','state2') NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL,
  `user_id` bigint(20) UNSIGNED DEFAULT NULL,
  `qty` varchar(10) NOT NULL,
  `author` varchar(150) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

